  `praise` int(9) unsigned DEFAULT NULL COMMENT '点赞',
  `product_id` bigint(20) unsigned DEFAULT NULL,
  `pics` varchar(4096) NOT NULL COMMENT '图片链接,英文逗号分隔',
  `pic_num` int(11) DEFAULT NULL COMMENT '图片数量',
